import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [viewMode, setViewMode] = useState('update');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState({ name: '', description: '', location: '', count: '' });

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory/');
      setInventory(res.data);
    } catch (err) {
      console.error('Error fetching inventory:', err);
    }
  };

  const handleAddItem = async () => {
    try {
      await axios.post('http://localhost:5050/api/inventory/add', newItem);
      fetchInventory();
      setShowAddForm(false);
      setNewItem({ name: '', description: '', location: '', count: '' });
    } catch (err) {
      console.error('Error adding item:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5050/api/inventory/delete/${id}`);
      fetchInventory();
    } catch (err) {
      console.error('Error deleting item:', err);
    }
  };

  return (
    <div style={styles.container}>
      <h2>ðŸ“¦ Inventory Items</h2>
      <div style={styles.toggleBtns}>
        <button onClick={() => setViewMode('view')} style={styles.toggleBtn}>View Mode</button>
        <button onClick={() => setViewMode('update')} style={styles.toggleBtn}>Update Mode</button>
      </div>

      {viewMode === 'update' && !showAddForm && (
        <button onClick={() => setShowAddForm(true)} style={styles.addBtn}>+ Add New Item</button>
      )}

      {showAddForm ? (
        <div style={styles.formWrapper}>
          <div style={styles.formContainer}>
            <h3>Add New Item</h3>
            <input
              style={styles.input}
              placeholder="Item Name"
              value={newItem.name}
              onChange={e => setNewItem({ ...newItem, name: e.target.value })}
            />
            <input
              style={styles.input}
              placeholder="Description"
              value={newItem.description}
              onChange={e => setNewItem({ ...newItem, description: e.target.value })}
            />
            <input
              style={styles.input}
              placeholder="Location"
              value={newItem.location}
              onChange={e => setNewItem({ ...newItem, location: e.target.value })}
            />
            <input
              style={styles.input}
              placeholder="Count"
              type="number"
              value={newItem.count}
              onChange={e => setNewItem({ ...newItem, count: e.target.value })}
            />
            <div>
              <button onClick={handleAddItem} style={styles.editBtn}>Add</button>
              <button onClick={() => setShowAddForm(false)} style={styles.deleteBtn}>Cancel</button>
            </div>
          </div>
        </div>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Description</th>
              <th>Location</th>
              <th>Count</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map(item => (
              <tr key={item._id}>
                <td>{item.name}</td>
                <td>{item.description || 'â€”'}</td>
                <td>{item.location}</td>
                <td>{item.count}</td>
                <td>
                  <button style={styles.editBtn}>Edit</button>
                  <button style={styles.deleteBtn} onClick={() => handleDelete(item._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    color: 'white'
  },
  toggleBtns: {
    marginBottom: '20px',
    display: 'flex',
    gap: '10px'
  },
  toggleBtn: {
    backgroundColor: '#1e90ff',
    border: 'none',
    color: 'white',
    padding: '8px 16px',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold'
  },
  addBtn: {
    backgroundColor: '#28a745',
    border: 'none',
    color: 'white',
    padding: '10px 16px',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold',
    marginBottom: '20px'
  },
  formWrapper: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '70vh'
  },
  formContainer: {
    backgroundColor: '#fff',
    padding: '25px',
    borderRadius: '8px',
    color: '#000',
    width: '350px',
    boxShadow: '0 0 10px rgba(0,0,0,0.3)'
  },
  input: {
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    border: '1px solid #ccc',
    borderRadius: '4px'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '10px',
    backgroundColor: '#fff',
    color: '#000'
  },
  editBtn: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '6px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '4px'
  },
  deleteBtn: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '6px 10px',
    border: 'none',
    borderRadius: '4px'
  }
};

export default InventoryPage;
