import React, { useEffect, useState } from 'react';
import axios from 'axios';

function RequestItemsPage() {
  const [inventory, setInventory] = useState([]);
  const [requests, setRequests] = useState({});
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('/api/inventory')
      .then(res => setInventory(res.data))
      .catch(err => console.error('Error fetching inventory:', err));
  }, []);

  const handleChange = (itemId, quantity) => {
    setRequests(prev => ({
      ...prev,
      [itemId]: quantity
    }));
  };

  const handleSubmit = () => {
    const payload = Object.entries(requests)
      .filter(([_, qty]) => qty > 0)
      .map(([itemId, qty]) => ({
        itemId,
        quantity: Number(qty),
        status: 'pending'
      }));

    if (payload.length === 0) {
      setMessage('Please select at least one item to request.');
      return;
    }

    axios.post('/api/requests', { requests: payload, requestedBy: 'stembassador' })
      .then(() => {
        setMessage('‚úÖ Request submitted successfully!');
        setRequests({});
      })
      .catch(err => {
        console.error(err);
        setMessage('‚ùå Error submitting request.');
      });
  };

  return (
    <div className="min-h-screen p-6" style={{ background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)', color: '#fff' }}>
      <h2 className="text-3xl font-semibold mb-6">üì¨ Request Inventory Items</h2>

      {inventory.length === 0 ? (
        <p>No items available.</p>
      ) : (
        <div className="bg-white rounded-lg p-4 shadow-lg text-black">
          <table className="table-auto w-full mb-4">
            <thead>
              <tr className="bg-gray-200">
                <th className="px-4 py-2 text-left">Item</th>
                <th className="px-4 py-2 text-left">Location</th>
                <th className="px-4 py-2 text-left">Available</th>
                <th className="px-4 py-2 text-left">Request Qty</th>
              </tr>
            </thead>
            <tbody>
              {inventory.map(item => (
                <tr key={item._id}>
                  <td className="border px-4 py-2">{item.itemName}</td>
                  <td className="border px-4 py-2">{item.location}</td>
                  <td className="border px-4 py-2">{item.count}</td>
                  <td className="border px-4 py-2">
                    <input
                      type="number"
                      min="0"
                      value={requests[item._id] || ''}
                      onChange={(e) => handleChange(item._id, e.target.value)}
                      className="border px-2 py-1 w-20 rounded"
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <button
        onClick={handleSubmit}
        className="bg-blue-600 text-white px-5 py-2 rounded hover:bg-blue-700 mt-4"
      >
        Submit Request
      </button>

      {message && <p className="mt-4 text-sm text-white font-medium">{message}</p>}
    </div>
  );
}

export default RequestItemsPage;
