// frontend/src/pages/InventoryPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InventoryPage = () => {
  const [items, setItems] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    quantity: '',
    category: '',
    description: ''
  });
  const [message, setMessage] = useState('');

  const fetchInventory = async () => {
    try {
      const res = await axios.get('http://localhost:5050/api/inventory');
      setItems(res.data);
    } catch (err) {
      console.error('Failed to fetch inventory:', err);
    }
  };

  useEffect(() => {
    fetchInventory();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5050/api/inventory/add', formData);
      setMessage(res.data.message);
      setFormData({ name: '', quantity: '', category: '', description: '' });
      fetchInventory();
    } catch (err) {
      console.error('Failed to add item:', err);
      setMessage('Error adding item');
    }
  };

  return (
    <div style={{ padding: '30px' }}>
      <h2>Inventory Management</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: '30px' }}>
        <input name="name" placeholder="Item Name" value={formData.name} onChange={handleChange} required />
        <input name="quantity" placeholder="Quantity" value={formData.quantity} onChange={handleChange} required />
        <input name="category" placeholder="Category" value={formData.category} onChange={handleChange} />
        <input name="description" placeholder="Description" value={formData.description} onChange={handleChange} />
        <button type="submit">Add Item</button>
      </form>

      {message && <p>{message}</p>}

      <h3>Current Inventory</h3>
      <ul>
        {items.map((item) => (
          <li key={item._id}>
            <strong>{item.name}</strong> - Qty: {item.quantity} | Category: {item.category} <br /> {item.description}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default InventoryPage;
